import React, { useEffect, useState } from "react";

type Pessoa = {
  id: number;
  nome: string;
  idade: number;
  enderecoId: number;
};

type Endereco = {
  id: number;
  rua: string;
  cidade: string;
  estado: string;
};

const API_PESSOAS = "http://localhost:3001/pessoas";
const API_ENDERECOS = "http://localhost:3001/enderecos";

const FormGroup = ({
  label,
  children,
}: {
  label: string;
  children: React.ReactNode;
}) => (
  <div style={{ marginBottom: "10px" }}>
    <label
      style={{ display: "block", fontWeight: "bold", marginBottom: "3px" }}
    >
      {label}
    </label>
    {children}
  </div>
);

const Pessoas = () => {
  const [pessoas, setPessoas] = useState<Pessoa[]>([]);
  const [enderecos, setEnderecos] = useState<Endereco[]>([]);

  // Form Pessoa
  const [idPessoa, setIdPessoa] = useState<number | null>(null);
  const [nome, setNome] = useState("");
  const [idade, setIdade] = useState<number | "">("");
  const [enderecoId, setEnderecoId] = useState<number | "">("");

  // Form Endereco
  const [idEndereco, setIdEndereco] = useState<number | null>(null);
  const [rua, setRua] = useState("");
  const [cidade, setCidade] = useState("");
  const [estado, setEstado] = useState("");

  // Mensagem de feedback
  const [mensagem, setMensagem] = useState("");

  const mostrarMensagem = (texto: string) => {
    setMensagem(texto);
    setTimeout(() => setMensagem(""), 3000);
  };

  useEffect(() => {
    fetch(API_PESSOAS)
      .then((r) => r.json())
      .then(setPessoas);
    fetch(API_ENDERECOS)
      .then((r) => r.json())
      .then(setEnderecos);
  }, []);

  const handleSalvarPessoa = (e: React.FormEvent) => {
    e.preventDefault();

    if (!nome.trim()) {
      alert("Nome é obrigatório");
      return;
    }
    if (idade === "" || idade <= 0) {
      alert("Idade deve ser maior que zero");
      return;
    }
    if (enderecoId === "") {
      alert("Selecione um endereço");
      return;
    }

    const pessoa: Pessoa = {
      id: idPessoa || 0,
      nome,
      idade: Number(idade),
      enderecoId: Number(enderecoId),
    };

    const url = idPessoa ? `${API_PESSOAS}/${idPessoa}` : API_PESSOAS;
    const method = idPessoa ? "PUT" : "POST";

    fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(pessoa),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao salvar pessoa");
        return res.json();
      })
      .then((data) => {
        if (idPessoa) {
          setPessoas((prev) => prev.map((p) => (p.id === idPessoa ? data : p)));
          mostrarMensagem("Pessoa atualizada com sucesso!");
        } else {
          setPessoas((prev) => [...prev, data]);
          mostrarMensagem("Pessoa cadastrada com sucesso!");
        }
        limparFormPessoa();
      })
      .catch(() => alert("Erro ao salvar pessoa"));
  };

  const limparFormPessoa = () => {
    setIdPessoa(null);
    setNome("");
    setIdade("");
    setEnderecoId("");
  };

  const prepararEdicaoPessoa = (p: Pessoa) => {
    setIdPessoa(p.id);
    setNome(p.nome);
    setIdade(p.idade);
    setEnderecoId(p.enderecoId);
  };

  const handleDeletarPessoa = (id: number) => {
    if (!window.confirm("Deseja realmente deletar esta pessoa?")) return;

    fetch(`${API_PESSOAS}/${id}`, { method: "DELETE" })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao deletar pessoa");
        return res.json();
      })
      .then(() => {
        setPessoas(pessoas.filter((p) => p.id !== id));
        mostrarMensagem("Pessoa deletada com sucesso!");
      })
      .catch(() => alert("Erro ao deletar pessoa"));
  };

  const handleSalvarEndereco = (e: React.FormEvent) => {
    e.preventDefault();

    if (!rua.trim()) {
      alert("Rua é obrigatória");
      return;
    }
    if (!cidade.trim()) {
      alert("Cidade é obrigatória");
      return;
    }
    if (!estado.trim()) {
      alert("Estado é obrigatório");
      return;
    }

    const endereco: Endereco = {
      id: idEndereco || 0,
      rua,
      cidade,
      estado,
    };

    const url = idEndereco ? `${API_ENDERECOS}/${idEndereco}` : API_ENDERECOS;
    const method = idEndereco ? "PUT" : "POST";

    fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(endereco),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao salvar endereço");
        return res.json();
      })
      .then((data) => {
        if (idEndereco) {
          setEnderecos((prev) =>
            prev.map((e) => (e.id === idEndereco ? data : e))
          );
          mostrarMensagem("Endereço atualizado com sucesso!");
        } else {
          setEnderecos((prev) => [...prev, data]);
          mostrarMensagem("Endereço cadastrado com sucesso!");
        }
        limparFormEndereco();
      })
      .catch(() => alert("Erro ao salvar endereço"));
  };

  const limparFormEndereco = () => {
    setIdEndereco(null);
    setRua("");
    setCidade("");
    setEstado("");
  };

  const prepararEdicaoEndereco = (e: Endereco) => {
    setIdEndereco(e.id);
    setRua(e.rua);
    setCidade(e.cidade);
    setEstado(e.estado);
  };

  const handleDeletarEndereco = (id: number) => {
    // Verifica se endereço está vinculado a alguma pessoa
    const estaEmUso = pessoas.some((p) => p.enderecoId === id);
    if (estaEmUso) {
      alert(
        "Este endereço está vinculado a uma pessoa e não pode ser deletado."
      );
      return;
    }

    if (!window.confirm("Deseja realmente deletar este endereço?")) return;

    fetch(`${API_ENDERECOS}/${id}`, { method: "DELETE" })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao deletar endereço");
        return res.json();
      })
      .then(() => {
        setEnderecos(enderecos.filter((e) => e.id !== id));
        mostrarMensagem("Endereço deletado com sucesso!");
      })
      .catch(() => alert("Erro ao deletar endereço"));
  };

  return (
    <div>
      <h1>Pessoas</h1>

      {mensagem && (
        <div style={{ color: "green", marginBottom: "10px" }}>{mensagem}</div>
      )}

      <ul>
        {pessoas
          .slice()
          .sort((a, b) => a.nome.localeCompare(b.nome))
          .map((p) => {
            const endereco = enderecos.find((e) => e.id === p.enderecoId);
            return (
              <li key={p.id}>
                {p.nome} ({p.idade}) —{" "}
                {endereco
                  ? `${endereco.rua}, ${endereco.cidade} - ${endereco.estado}`
                  : "Endereço não encontrado"}
                {"  "}
                <button onClick={() => prepararEdicaoPessoa(p)}>
                  Editar
                </button>{" "}
                <button onClick={() => handleDeletarPessoa(p.id)}>
                  Excluir
                </button>
              </li>
            );
          })}
      </ul>

      <h2>{idPessoa ? "Editar Pessoa" : "Cadastrar Pessoa"}</h2>
      <form onSubmit={handleSalvarPessoa}>
        <FormGroup label="Nome">
          <input
            type="text"
            value={nome}
            placeholder="Nome"
            onChange={(e) => setNome(e.target.value)}
            required
            style={{ borderColor: nome.trim() === "" ? "red" : undefined }}
          />
        </FormGroup>
        <FormGroup label="Idade">
          <input
            type="number"
            value={idade}
            placeholder="Idade"
            onChange={(e) =>
              setIdade(e.target.value === "" ? "" : Number(e.target.value))
            }
            required
            min={1}
            style={{
              borderColor: idade === "" || idade <= 0 ? "red" : undefined,
            }}
          />
        </FormGroup>
        <FormGroup label="Endereço">
          <select
            value={enderecoId}
            onChange={(e) =>
              setEnderecoId(e.target.value === "" ? "" : Number(e.target.value))
            }
            required
            style={{ borderColor: enderecoId === "" ? "red" : undefined }}
          >
            <option value="">Selecione</option>
            {enderecos
              .slice()
              .sort((a, b) => a.rua.localeCompare(b.rua))
              .map((end) => (
                <option key={end.id} value={end.id}>
                  {`${end.rua}, ${end.cidade} - ${end.estado}`}
                </option>
              ))}
          </select>
        </FormGroup>
        <button type="submit">{idPessoa ? "Atualizar" : "Cadastrar"}</button>{" "}
        {idPessoa && (
          <button type="button" onClick={limparFormPessoa}>
            Cancelar
          </button>
        )}
      </form>

      <hr />

      <h1>Endereços</h1>

      <ul>
        {enderecos
          .slice()
          .sort((a, b) => a.rua.localeCompare(b.rua))
          .map((e) => (
            <li key={e.id}>
              {e.rua}, {e.cidade} - {e.estado}{" "}
              <button onClick={() => prepararEdicaoEndereco(e)}>Editar</button>{" "}
              <button onClick={() => handleDeletarEndereco(e.id)}>
                Excluir
              </button>
            </li>
          ))}
      </ul>

      <h2>{idEndereco ? "Editar Endereço" : "Cadastrar Endereço"}</h2>
      <form onSubmit={handleSalvarEndereco}>
        <FormGroup label="Rua">
          <input
            type="text"
            value={rua}
            placeholder="Rua"
            onChange={(e) => setRua(e.target.value)}
            required
            style={{ borderColor: rua.trim() === "" ? "red" : undefined }}
          />
        </FormGroup>
        <FormGroup label="Cidade">
          <input
            type="text"
            value={cidade}
            placeholder="Cidade"
            onChange={(e) => setCidade(e.target.value)}
            required
            style={{ borderColor: cidade.trim() === "" ? "red" : undefined }}
          />
        </FormGroup>
        <FormGroup label="Estado">
          <input
            type="text"
            value={estado}
            placeholder="Estado"
            onChange={(e) => setEstado(e.target.value)}
            required
            style={{ borderColor: estado.trim() === "" ? "red" : undefined }}
          />
        </FormGroup>
        <button type="submit">{idEndereco ? "Atualizar" : "Cadastrar"}</button>{" "}
        {idEndereco && (
          <button type="button" onClick={limparFormEndereco}>
            Cancelar
          </button>
        )}
      </form>
    </div>
  );
};

export default Pessoas;
